import React, { Component } from 'react';
import './Product.css';

import api from '../services/api';

export default class Products extends Component {
  state = {
    _products : [
    {
      title: "Skol 269ml",
      images: {
        url: "skol.jpg"
      },
      productVariants:[
        {
          old_price: 2.09,
          price: 1.99,
        }
      ]
    },
    {
      title: "Skol 269ml",
      images: {
        url: "skol.jpg"
      },
      productVariants:[
        {
          old_price: 2.09,
          price: 1.99,
        }
      ]
    },            
  ]};    

  render () {
    const { _products } = this.state;
    return (
    <div className="main-container">
      <div className="products">
      <strong className="category">Cervejas</strong>
      <div className="cards">        
        {_products.map(product => (        
        <div className="card">
          <div className="img">
            <img src={product.images.url} />
          </div>
          <footer className="card-footer">
            <div className="card-footer-info">
              <strong>{product.title}</strong>
              <div className="price">
                <span><del>R$ {product.productVariants[0].old_price}</del></span>
                <strong>R$ {product.productVariants[0].price}</strong>
              </div>  
            </div>
            <div className="card-footer-qtd">
              <div className="card-footer-total">
                <div className="desc">
                  Total
                </div>
                <div  className="value">
                  R$ 10,99
                </div>
              </div>
              <div className="qtd">
               <a className="btn btn-qtd-change">-</a>
               <input type="number" className="qtd-input" />
               <a className="btn btn-qtd-change">+</a>
              </div>                 
              <a className="btn btn-add">Adicionar ao carrinho</a>              
            </div>
          </footer>
        </div>
        ))}                            
      </div>
    </div>
    </div>
    )
  }
}
