import React from 'react';
import './App.css';

import Routes from './routes';

function App() {

  function themeChange(e) {
    if (e.target.checked) {
      document.documentElement.setAttribute('data-theme', 'dracula');
    }
    else {
      document.documentElement.removeAttribute('data-theme');
    }
  }

  const inputOnChange = (e) => {
    const productList = document.querySelector('.products');
    const addressList = document.querySelector('.address-list');
//    productList.classList.add('cards-opacity');
    addressList.classList.add('display-flex');
  }

  const inputOnBlur = () => {
    const productList = document.querySelector('.products');
    const addressList = document.querySelector('.address-list');    
  //  productList.classList.remove('cards-opacity');
    addressList.classList.remove('display-flex');
  }   

  var inputVal = 'George';

  return (
    <div className="app">
      <header>
        <link rel="shortcut icon" href="favicon.ico" type="image/x-icon" />
        <link href="https://fonts.googleapis.com/css?family=Montserrat&display=swap" rel="stylesheet" />
        <div className="header">          
          <div className="small-area">
            <img className="logo" src="logo-completo.png" />
          </div>
          <div className="large-area">
            <input type="text" id="address-input" value="{this.inputVal}" className="header-input" onChange={(e) => inputOnChange()} onBlur={() => inputOnBlur()}value="" placeHolder="Digite o seu endereço" />           
          </div>
          <div className="header-small-cart">
            <div className="header-small-cart-img">
              <img className="header-small-cart-img" src="cart.png" /> 
            </div>
            <div className="header-small-cart-value">
              <span>R$ 286,98</span>
            </div>
          </div>
        </div>
        <div className="header-small">          
          <img className="logo" src="small-logo.png" />
          <input type="text" id="address-input" value="{this.inputVal}" className="header-input" onChange={(e) => inputOnChange()} onBlur={() => inputOnBlur()}value="" placeHolder="Digite o seu endereço" />
          <div className="header-small-cart">
            <div className="header-small-cart-img">
              <img className="header-small-cart-img" src="cart.png" /> 
            </div>
            <div className="header-small-cart-value">
              <span>R$ 286,98</span>
            </div>
          </div>
        </div>
      </header>
      <body className="body">
        <div className="address-list">
          <div className="address-item">
            <div className="location-icon">
              <img className="location-icon" src="location-icon.png" />
            </div>
            <div className="address-text">
              <div className="address-text-first-line">
                Rua George Eastman
              </div>
              <div className="address-text-second-line">
                Vila Tramontano - São Paulo - SP, Brasil
              </div>
            </div>
          </div>
          <hr className="address-list-divider" />
          <div className="address-item">
            <div className="location-icon">
              <img className="location-icon" src="location-icon.png" />
            </div>
            <div className="address-text">
              <div className="address-text-first-line">
                Rua George Eastman aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
              </div>
              <div className="address-text-second-line">
                Vila Tramontano - São Paulo - SP, Brasil
              </div>
            </div>
          </div>          
        </div>
        <Routes />
      </body>
      <footer className="footer">
      </footer>
    </div>
  );
}

export default App;
