import React from 'react';
import './App.css';

import Routes from './routes';

function App() {

  function themeChange(e) {
    if (e.target.checked) {
      document.documentElement.setAttribute('data-theme', 'dracula');
    }
    else {
      document.documentElement.removeAttribute('data-theme');
    }
  }

  const inputOnChange = (e) => {
    //console.log(e.target.value);
    const cardsDiv = document.querySelector('.products');
    console.log(cardsDiv);    
    cardsDiv.classList.add('cards-opacity');
  }

  const inputOnBlur = () => {
    const cardsDiv = document.querySelector('.products');
    cardsDiv.classList.remove('cards-opacity');
  }   

  var inputVal = 'George';

  return (
    <div className="app">
      <header>
        <link rel="shortcut icon" href="favicon.ico" type="image/x-icon" />
        <link href="https://fonts.googleapis.com/css?family=Montserrat&display=swap" rel="stylesheet" />
        <div className="header">          
          <div className="small-area">
            <img className="logo" src="logo-completo.png" />
          </div>
          <div className="large-area">
            <input type="text" id="address-input" value="{this.inputVal}" className="header-input" onChange={(e) => inputOnChange()} onBlur={() => inputOnBlur()}value="" placeHolder="Digite o seu endereço" />           
          </div>
          <div className="header-small-cart">
            <div className="header-small-cart-img">
              <img className="header-small-cart-img" src="cart.png" /> 
            </div>
            <div className="header-small-cart-value">
              <span>R$ 286,98</span>
            </div>
          </div>
        </div>
        <div className="header-small">          
          <img className="logo" src="small-logo.png" />
          <input type="text" id="address-input" value="{this.inputVal}" className="header-input" onChange={(e) => inputOnChange()} onBlur={() => inputOnBlur()}value="" placeHolder="Digite o seu endereço" />
          <div className="header-small-cart">
            <div className="header-small-cart-img">
              <img className="header-small-cart-img" src="cart.png" /> 
            </div>
            <div className="header-small-cart-value">
              <span>R$ 286,98</span>
            </div>
          </div>
        </div>
      </header>
      <body className="body">
        <div className="address-list">
          <div className="address-item">
            <div>
              <img src="location.png" />
            </div>
            <div>
              Rua George Eastman - Vila Tramontano - São Paulo - SP, Brasil
            </div>
          </div>
        </div>
        <Routes />
      </body>
      <footer className="footer">
      </footer>
    </div>
  );
}

export default App;
