import React from 'react';
import './App.css';

import Routes from './routes';

function App() {

  function themeChange(e) {
    if (e.target.checked) {
      document.documentElement.setAttribute('data-theme', 'dracula');
    }
    else {
      document.documentElement.removeAttribute('data-theme');
    }
  }

  /*  return (
    <div className="app">
      <div className="theme-switch-wrapper">
        <label className="theme-switch">
          <input type="checkbox" id="checkbox" onChange={themeChange} />
          <div className="slider round"></div>
        </label>
      </div>
      <Routes />
    </div>
  );*/

  const cardsDiv = document.querySelector('.cards');
  const inputOnChange = () => {
    cardsDiv.classList.add = 'cards-opacity';
    console.log('oi');
  }

  const inputOnBlur = () => {
    console.log('xau');
  }  

  return (
    <div className="app">
      <header>
      <link rel="shortcut icon" href="favicon.ico" type="image/x-icon" />
        <link href="https://fonts.googleapis.com/css?family=Montserrat&display=swap" rel="stylesheet" />
        <div className="header">          
          <div className="small-area logo">
            <img className="logo" src="logo-completo.png" />
          </div>
          <div className="large-area">
            <input type="text" id="address-input" className="header-input" onChange={() => inputOnChange()} onBlur={() => inputOnBlur()}value="" placeHolder="Digite o seu endereÃ§o" />           
          </div>
          <div className="small-area flex-row">
            <img className="cart" src="cart.png" />            
            <div className="cart-total">
              <span>
                Total no carrinho           
              </span>
              <input value="R$ 286,98" className="qtd-input" />
            </div>            
          </div>          
        </div>
        <div className="header-small">
        </div>
      </header>
      <body className="body">
        <Routes />
      </body>
      <footer className="footer">
      </footer>
    </div>
  );
}

export default App;
